mapper.select:
select * from (select rownum as rn, c_id, c_name, c_year1, c_year2,c_brand, c_ft, c_bt, c_print, c_file, cb_name from (select * from car INNER JOIN car_brand ON c_brand = cb_name where
c_brand like '%'||#{c_brand}||'%' and
c_name like '%'||#{c_name}||'%' ORDER BY c_year2 desc) )WHERE rn >= #{start} and rn <=#{end}

mapper.update:
update Car set
c_name = #{c_name}, c_year1 = #{c_year1}, c_year2 = #{c_year2},
c_brand = #{c_brand}, c_ft = #{c_ft},
c_bt = #{c_bt}, c_print = #{c_print}, c_file = #{c_file}
where c_id = #{c_id}

