mapper.select:
SELECT * FROM ( SELECT rownum as rn, c.c_id, c.c_name, c.c_year1, c.c_year2, c.c_option, c.c_brand, c.c_ft, c.c_bt, c.c_print, c.c_file, cb.cb_name, cb.cb_order FROM Car c INNER JOIN car_brand cb ON c.c_brand = cb.cb_name WHERE c.c_name LIKE '%' || #{carnameInput} || '%'
AND c.c_brand = #{carbrandInput}
ORDER BY TO_NUMBER(cb.cb_order) ASC, c.c_brand ASC ) WHERE rn >= #{start} and rn <= #{end}

mapper.update:
update Car 
set c_brand = #{new_cb_name}
where c_brand = #{old_cb_name}

update car_brand
    set 
        cb_name = #{new_cb_name},

        cb_order = #{cb_order}

    where cb_name = #{old_cb_name}
