mapper.select:
		select *
        from (
			select	rownum as rownumber,
					q_no,
					q_title,
					q_date
			from (
				select *
				from qna, auth_user
				where	u_id = #{u_id} and
						q_id = #{u_id}
				order by q_date desc
			)
        )
        where rownumber >= #{begin} and rownumber <= #{last}

mapper.update:
update
qna
set	q_title = #{q_title},
	q_txt = #{q_txt}
where q_no = #{q_no}
