mapper.select:
		select *
        from (
			select	rownum as rownumber,
					f_title,
					f_txt
			from (
				select *
				from faq


						where f_sortation like '%'||#{f_sortation}||'%'




							where	f_title like '%'||#{faqSearch}||'%' or
									f_txt like '%'||#{faqSearch}||'%'


							where	f_title like '%'||#{faqSearch}||'%'


							where	f_txt like '%'||#{faqSearch}||'%'




			)
        )
        where rownumber >= #{begin} and rownumber <= #{last}

		select *
        from (
			select	rownum as rownumber,
					q_no,
					q_title,
					q_date
			from (
				select *
				from qna, auth_user
				where	u_id = #{u_id} and
						q_id = #{u_id}
				order by q_date desc
			)
        )
        where rownumber >= #{begin} and rownumber <= #{last}

mapper.update:
update
qna
set	q_title = #{q_title},
	q_txt = #{q_txt}
where q_no = #{q_no}
